import { SmartAccount } from "./SmartAccount";
import {
	solidityPacked,
	AbiCoder,
	keccak256,
	solidityPackedKeccak256,
} from "ethers";
import type { BigNumberish, BytesLike } from "ethers";
import { SimpleAccountFactory } from "../factory/SimpleAccountFactory";
import { SmartAccountFactory } from "../factory/SmartAccountFactory";
import { getCallData } from "../utils";
import { AbiInputValue } from "../types";

export class SimpleAccount extends SmartAccount {
	readonly entrypointAddress: string;
	readonly simpleAccountFactory: SimpleAccountFactory;
	readonly batchExecutorFunctionSelector: string;
	readonly batchExecutorFunctionInputAbi: string[];

	constructor(
		singletonAddress: string = "0x8ABB13360b87Be5EEb1B98647A016adD927a136c",
		entrypointAddress: string = "0x5FF137D4b0FDCD49DcA30c7CF57E578a026d2789",
		simpleAccountFactory: SmartAccountFactory = new SimpleAccountFactory(),
	) {
		const proxyByteCode =
			"";
		const initializerFunctionSelector = "0xc4d66de8"; //initialize
		const initializerFunctionInputAbi = ["address"];

		const executorFunctionSelector = "0xb61d27f6"; //execute
		const executorFunctionInputAbi = [
			"address", //dest
			"uint256", //value
			"bytes", //func
		];
		super(
			singletonAddress,
			proxyByteCode,
			initializerFunctionSelector,
			initializerFunctionInputAbi,
			executorFunctionSelector,
			executorFunctionInputAbi,
		);
		this.entrypointAddress = entrypointAddress;
		this.simpleAccountFactory = simpleAccountFactory;

		this.batchExecutorFunctionSelector = "0x18dfb3c7"; //executeBatch
		this.batchExecutorFunctionInputAbi = ["address[]", "bytes[]"];
	}

	createNewAccount(
		owner: string,
		c2nonce: BigNumberish = 0,
	): [string, BytesLike] {
		const initializerFunctionInputParameters = [
			owner, //owner
		];

		const initializerCallData = this.getInitializerCallData(
			initializerFunctionInputParameters,
		);

		const generatorFunctionInputParameters = [
			owner,
			c2nonce,
		] as AbiInputValue[];

		const factoryGeneratorFunctionCallData =
			this.simpleAccountFactory.getFactoryGeneratorFunctionCallData(
				generatorFunctionInputParameters,
			);
		const sender = this.getProxyAddress(
			initializerCallData,
			this.simpleAccountFactory.address,
			c2nonce,
		);

		return [sender, factoryGeneratorFunctionCallData];
	}

	getProxyAddress(
		initializerCallData: BytesLike,
		factoryAddress: string,
		c2Nonce: BigNumberish,
	): string {
		const abiCoder = AbiCoder.defaultAbiCoder();
		const salt = abiCoder.encode(["uint256"], [c2Nonce]);

		const initData = abiCoder.encode(
			["address", "bytes"],
			[this.singletonAddress, initializerCallData],
		);

		const initHash = keccak256(
			solidityPacked(["bytes", "bytes"], [this.proxyByteCode, initData]),
		);

		const proxyAdd = solidityPackedKeccak256(
			["bytes1", "address", "bytes32", "bytes32"],
			["0xff", factoryAddress, salt, initHash],
		).slice(-40);
		return "0x" + proxyAdd;
	}

	createSendEthCallData(to: string, value: BigNumberish): BytesLike {
		return this.createCallData(to, value, "0x");
	}

	createCallData(to: string, value: BigNumberish, data: BytesLike): BytesLike {
		const executorFunctionCallData = this.getExecutorCallData([
			to,
			value,
			data,
		]);
		return executorFunctionCallData;
	}

	createBatchCallData(to_list: string[], data_list: BytesLike[]): BytesLike {
		const callData = getCallData(
			this.batchExecutorFunctionSelector,
			this.batchExecutorFunctionInputAbi,
			[to_list, data_list],
		);

		return callData;
	}
}
